import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../student.service';
import { Student } from '../students';

@Component({
  selector: 'app-edit-student',
  templateUrl: './edit-student.component.html',
  styleUrls: ['./edit-student.component.css']
})
export class EditStudentComponent {
  //Napisac request typu GET do pobrania danych studenta o określonym id - StudentService
  //Upewnimy sie ze dane sa w porzadku 
  //Zbudujemy draft formularza edycji
  //Bootstrap -> ostylowanie formularza
  student!: Student;

  constructor(private activatedRoute: ActivatedRoute, private studentService: StudentService) {
    this.activatedRoute.params.subscribe(param => {
      alert('Przekazano parametr: ' + param["id"]);
      this.studentService.getStudentByID(param["id"]).subscribe(data => {
        this.student = data;
        console.log(this.student);
      })
    });

  }

}
<h4>Edytuj studenta - {{student.name}}</h4>

<form>
    <div>
        <label class="form-label">Imię i nazwisko studenta: </label>
        <input name="studentName" [(ngModel)]="student.name" class="form-control" type="text">
    </div>

    <div>
        <label class="form-label">Adres email: </label>
        <input name="studentEmail" [(ngModel)]="student.email" class="form-control" type="email">
    </div>

    <div style="margin-top: 15px;">
        <button class="btn btn-outline-success">Zapisz</button>
    </div>
</form>
