import { Component } from '@angular/core';
import { StudentService } from '../student.service';
import { Student } from '../students';

@Component({
  selector: 'app-students-list',
  templateUrl: './students-list.component.html',
  styleUrls: ['./students-list.component.css']
})
export class StudentsListComponent {
  isLoaded = false;
  btnTitle = "Show";
  students: Student[] = [];

  isDataSearching = false;

  constructor(private studentService: StudentService) {

  }

  search() {
    console.log("Kliknelo przycisk Search");
    this.isLoaded = !this.isLoaded;

    if (this.isLoaded) {
      this.btnTitle = "Hide"

      this.isDataSearching = true;

      this.studentService.getStudents().subscribe(data => {
        console.log("wewnatrz subscribe");
        console.log(data);
        this.students = data

        this.isDataSearching = false;
      });
      console.log("poza subscribe");

    } else {
      this.btnTitle = "Show";
    }

  }

}
<section>

  <button class="btn btn-primary" (click)="search()">{{btnTitle}}
    <span *ngIf="isDataSearching" class="spinner-border spinner-border-sm" aria-hidden="true"></span>
    <!-- <span class="visually-hidden" role="status">Loading...</span> -->
  </button>

    <table class="table table-striped table-hover table-dark">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name and surname</th>
          <th>E-mail address</th>
          <!-- <th>Actions</th> -->
        </tr>
      </thead>
      <tbody *ngIf="isLoaded == true">
        <tr [ngClass]="{'table-warning': student.id == 2, 'table-success': student.id > 8}" *ngFor="let student of students">
          <td>{{student.id}}</td>
          <td>{{student.name | uppercase}}</td>
          <td>{{student.email}}</td>
          <!-- <td>
            <a href="#" class="button button-green">Edytuj</a>
            <button class="button button-red">Usuń</button>
          </td> -->
        </tr>
      </tbody>
    </table>

    <div *ngFor="let student of students">
      <h4>{{student.name}}</h4>
      <p>{{student.email}}</p>
    </div>
  </section>
